<?php	$tpi_facebook_image = get_post_meta($post->ID, 'tpi_facebook_image', true);		if($tpi_facebook_image) {		if($image_data = tfb_get_image_data($tpi_facebook_image)) {				$tmp_array = split("\n", $image_data['image']->name);		$image_name_text = array();		foreach($tmp_array as $tmp) {			if($tmp != '') {				$image_name_text[] = $tmp;			}		}						echo '<div class="post_facebook_image lightbox">';		echo "<h3>{$image_name_text[0]}</h3>";		unset($image_name_text[0]);		echo '<a href="'.$image_data['image']->source.'"><img src="'.$image_data['image']->picture.'" src="'.$image_data['image']->name.'" /></a>';		echo '<p class="image_text" >' . implode('<br/>', $image_name_text) . '</p>';		echo '</div>';				$comments_html = array();		foreach($image_data['comments'] as $tmp) {			$fb_img = TFB_GRAPH_URL . $tmp->from_id . '/picture';			$comments_html[] = '<li  class="comment byuser even thread-even depth-1">				<div class="comment-body" id="div-comment-6560">					<div class="comment-author vcard">					<img height="32" width="32" class="avatar avatar-32 photo" src="'.$fb_img.'" alt="facebook avatar">		<cite class="fn">'.$tmp->from_name.'</cite> 					<span class="says">dice:</span></div>					<div class="comment-meta commentmetadata">'.date('d/m/Y H:i', strtotime($tmp->created)).' </div>					<p>'.$tmp->message.'</p>				</li>';		}				if(count($comments_html) > 0) {			echo '<ol class="commentlist fb-commentlist">' . implode("\n\n", $comments_html) . '</ol>';		}?><?php										}	}